// Modify the BotCollection component to handle the selected bot
import React, { useEffect, useState } from 'react';
import './BotCollection.css';

function BotCollection({ selectedBot }) {
  const [bots, setBots] = useState([]);
  const [army, setArmy] = useState([]);

  useEffect(() => {
    async function fetchBots() {
      const response = await fetch('http://localhost:3000/bots');
      const bots = await response.json();
      setBots(bots);
    }
    fetchBots();
  }, []);

  const addToArmy = (bot) => {
    if (!army.includes(bot)) {
      setArmy([...army, bot]);
    }
  };

  const removeFromArmy = (bot) => {
    setArmy(army.filter((b) => b !== bot));
  };

  return (
    <div className="bot-list-container">
      {bots.map((bot, index) => (
        <div key={index} className="bot-item">
          <img src={bot.avatar_url} alt={`Bot ${index}`} />
          <p>Name: {bot.name}</p>
          <p>Health: {bot.health}</p>
          <p>Damage: {bot.damage}</p>
          <p>Armor: {bot.armor}</p>
          <p>Bot_Class: {bot.bot_class}</p>
          <p className="bot-phrase">Phrase: {bot.catchphrase}</p>
          <button
            className="button"
            onClick={() => addToArmy(bot)}
          >
            Add to Army
          </button>
          {selectedBot && selectedBot.id === bot.id && (
            <button
              className="button"
              onClick={() => selectedBot(bot)}
            >
              View Specs
            </button>
          )}
        </div>
      ))}
    </div>
  );
}

export default BotCollection;
