import React, { useState } from 'react';
import BotCollection from './BotCollection';
import YourBotArmy from './YourBotArmy';
import BotSpecs from './BotSpecs'; // Import the BotSpecs component
import SortBar from './SortBar'; // Import the SortBar component
import './App.css';

function App() {
  const [selectedBot, setSelectedBot] = useState(null);
  const [sortedBy, setSortedBy] = useState(''); // Initialize with an empty string for no sorting

  const handleSort = (property) => {
    setSortedBy(property);
  };

  return (
    <div>
      <h1>Bot Battlr</h1>
      <SortBar onSort={handleSort} /> {/* Include the SortBar component */}
      <div className="app-container">
        <BotCollection selectedBot={selectedBot} />
        {selectedBot && <BotSpecs bot={selectedBot} />} {/* Display BotSpecs when a bot is selected */}
        <YourBotArmy />
      </div>
    </div>
  );
}

export default App;
