// App.js (your main application file)
import React, { useState } from 'react';
import BotCollection from './BotCollection';
import YourBotArmy from './YourBotArmy';

function App() {
  const [yourArmyData, setYourArmyData] = useState([]);

  // Define a function to remove a bot from the army
  const removeFromArmy = (botToRemove) => {
    setYourArmyData(yourArmyData.filter((bot) => bot.id !== botToRemove.id));
  };

  // Define a function to discharge a bot
  const dischargeBot = (botToDischarge) => {
    // Perform the necessary logic to discharge the bot, which may include backend changes.
    // After discharging, you can remove the bot from your army.
    setYourArmyData(yourArmyData.filter((bot) => bot.id !== botToDischarge.id));
  };

  return (
    <div>
      <h1>Bot Battlr</h1>
      <BotCollection />
      <YourBotArmy army={yourArmyData} removeFromArmy={removeFromArmy} dischargeBot={dischargeBot} />
    </div>
  );
}

export default App;
